mapper.select:
/**cfDiegoDeploy.selectCfDiegoInfoByPlaform**/ Select a.id as id ,a.iaas_type as iaas_type
,c.id as cf_id ,c.iaas_type as iaas_type ,c.deployment_name as cf_deployment_name ,c.director_uuid as director_uuid ,c.release_name as release_name ,c.release_version as release_version ,c.app_ssh_fingerprint as app_ssh_fingerprint ,c.dea_memory_mb as dea_memory_mb ,c.dea_disk_mb as dea_disk_mb ,c.domain as domain ,c.description as description ,c.domain_organization as domain_organization ,c.proxy_static_ips as proxy_static_ips ,c.ssl_pem_pub as ssl_pem_pub ,c.ssl_pem_rsa as ssl_pem_rsa ,c.login_secret as login_secret ,c.encrypt_keys as encrypt_keys ,c.deployment_file as cf_deployment_file ,c.deploy_status as cf_deploy_status ,c.task_id as cf_task_id
,d.id as diego_id ,d.deployment_name as diego_deployment_name ,d.diego_release_name as diego_release_name ,d.diego_release_version as diego_release_version ,d.cflinuxfs2_rootfs_release_name as cflinuxfs2_rootfs_release_name ,d.cflinuxfs2_rootfs_release_version as cflinuxfs2_rootfs_release_version ,d.garden_release_name as garden_release_name ,d.garden_release_version as garden_release_version ,d.etcd_release_name as etcd_release_name ,d.etcd_release_version as garden_release_version ,d.diego_encryption_keys as diego_encryption_keys ,d.deployment_file as diego_deployment_file ,d.deploy_status as diego_deploy_status ,d.task_id as diego_task_id
,a.create_date as create_date ,a.create_user_id as create_user_id ,a.update_date as update_date ,a.update_user_id as update_user_id From ieda_cf_diego a
,ieda_cf c Where c.id = a.cf_id And c.id = #{id}
,ieda_diego d Where d.id = a.diego_id And d.id = #{id}

mapper.insert:
/**cfDiegoDeploy.insertCfDiegoInfo**/
Insert Into ieda_cf_diego(
	 id
	,cf_id
	,diego_id
	,iaas_type
	,create_user_id
	,create_date
	,update_user_id
	,update_date
)Values(
	#{cfDiego.id}
	,#{cfDiego.cfVo.id}
	,#{cfDiego.diegoVo.id}
	,#{cfDiego.iaasType}
	,#{cfDiego.createUserId}
	,now()
	,#{cfDiego.updateUserId}
	,now()
)

		SELECT LAST_INSERT_ID();

mapper.update:
/**cfDiegoDeploy.updateCfDiegoInfo**/
Update ieda_cf_diego Set
	 update_user_id = #{cfDiego.updateUserId}
	,update_date = now()

		 ,diego_id = #{cfDiego.diegoVo.id}

Where id = #{cfDiego.id}
